import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes, css } from 'styled-components';
import { qaApi, QAQuestion, QASubmission } from '../api/qaApi';
import { useVoiceToText, RecordingStatus } from '../hooks/useVoiceToText';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.2); }
  100% { transform: scale(1); }
`;

// Styled Components
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  background-color: #f0f2f5;
  padding: 20px;
  animation: ${fadeIn} 0.8s ease-out;
`;

const QAContainer = styled.div`
  width: 100%;
  max-width: 700px;
  height: 100%;
  max-height: 800px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const QuestionBox = styled.div`
  padding: 40px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  text-align: center;
  margin-bottom: 20px;
  animation: ${fadeIn} 0.5s ease-out forwards;

  h2 {
    font-size: 1.5rem;
    font-weight: 600;
    color: #343a40;
    line-height: 1.6;
  }
`;

const InputSection = styled.div`
  width: 100%;
`;

const InputBox = styled.div`
  position: relative;
  padding: 20px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const StyledTextarea = styled.textarea`
  width: 100%;
  height: 150px;
  border: none;
  background-color: transparent;
  resize: none;
  font-size: 1rem;
  font-family: inherit;
  color: #495057;
  padding-right: 50px; // for microphone button

  &:focus {
    outline: none;
  }
`;

const MicButton = styled.button<{ $status: RecordingStatus }>`
  position: absolute;
  right: 20px;
  bottom: 65px;
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1.5rem;
  color: #868e96;
  transition: color 0.2s;

  &:hover {
    color: #495057;
  }

  // ÎÖπÏùå ÏÉÅÌÉúÏóê Îî∞Î•∏ ÎèôÏ†Å Ïä§ÌÉÄÏùº
  ${({ $status }) => $status === "recording" && css`
    color: #fff;
    background-color: #e03131; // Îπ®Í∞ÑÏÉâ Î∞∞Í≤Ω
    animation: ${pulse} 1.5s infinite;
  `}
`;

