import React, { useEffect, useRef, useState } from 'react';
import { Conversation, Persona, GlobalMessage, TrainingThread } from '../types';
import Message from './Message';
import MessageInput from './MessageInput';
import TrainingCategoryView from './TrainingCategoryView';
import InterpretationView from './InterpretationView';
import ProceedView from './ProceedView';
import SentenceView from './SentenceView';
import PersonaAdminPanel from './PersonaAdminPanel';
import LoadingMessage from './LoadingMessage';
import { chatApi } from '../api/chatApi';
import {
  MainArea,
  ChatHeader,
  ChatHeaderTitle,
  ChatContent,
  LoadingSpinner,
  ErrorMessage,
  EmptyState,
  EmptyStateTitle,
  EmptyStateText,
} from '../styles/GlobalStyle';

interface ChatAreaProps {
  selectedConversation: string | null;
  conversations: Record<string, Conversation>;
  personas: Record<string, Persona>;
  globalMessages: GlobalMessage[];
  isLoading: boolean;
  error: string | null;
  onSendInitialMessage: (message: string) => Promise<boolean>;
  onContinueConversation: (conversationId: string, message: string) => Promise<boolean>;
  onClearError: () => void;
  selectedPersonaItem?: string | null; // ÌõàÏäµ Î™®Îìú ÌôïÏù∏Ïö©
  onSelectPersona?: (personaId: string) => void; // ÌéòÎ•¥ÏÜåÎÇò ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  onInterpretationComplete: (personaId: string, interpretation: string) => void; // Ìï¥ÏÑù ÏôÑÎ£å Ìï∏Îì§Îü¨
  selectedConversationItem?: string | null; // ÏÑ†ÌÉùÎêú ÎåÄÌôî Ìï≠Î™©
  currentInterpretation?: {
    personaId: string;
    personaName: string;
    content: string;
    messages: Array<{
      role: 'user' | 'assistant';
      content: string;
      timestamp: string;
    }>;
  } | null;
  onInterpretationMessage: (message: string) => Promise<boolean>; // Ìï¥ÏÑù Ï±ÑÌåÖ Î©îÏãúÏßÄ Ìï∏Îì§Îü¨
  onUpdateInterpretation?: (updatedInterpretation: {
    personaId: string;
    personaName: string;
    content: string;
    messages: Array<{
      role: 'user' | 'assistant';
      content: string;
      timestamp: string;
    }>;
  }) => void; // Ìï¥ÏÑù ÏóÖÎç∞Ïù¥Ìä∏ Ìï∏Îì§Îü¨
  onUpdateConversation?: (updatedConversation: any) => void; // ÎåÄÌôî ÏóÖÎç∞Ïù¥Ìä∏ Ìï∏Îì§Îü¨
}

const ChatArea: React.FC<ChatAreaProps> = ({
  selectedConversation,
  conversations,
  personas,
  globalMessages,
  isLoading,
  error,
  onSendInitialMessage,
  onContinueConversation,
  onClearError,
  selectedPersonaItem,
  onSelectPersona,
  onInterpretationComplete,
  selectedConversationItem,
  currentInterpretation,
  onInterpretationMessage,
  onUpdateInterpretation,
  onUpdateConversation,
}) => {
  const chatContentRef = useRef<HTMLDivElement>(null);
  const currentConversation = selectedConversation ? conversations[selectedConversation] : null;

  // Ìé∏Ïßë ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);

  // ÌõàÏäµ Í¥ÄÎ†® ÏÉÅÌÉú
  const [threads, setThreads] = useState<TrainingThread[]>([]);
  const [proceedContent, setProceedContent] = useState<string>('');
  const [sentenceContent, setSentenceContent] = useState<string>('');

  // Î©îÏãúÏßÄÍ∞Ä Ï∂îÍ∞ÄÎê† ÎïåÎßàÎã§ Ïä§ÌÅ¨Î°§ÏùÑ Îß® ÏïÑÎûòÎ°ú
  useEffect(() => {
    if (chatContentRef.current) {
      chatContentRef.current.scrollTop = chatContentRef.current.scrollHeight;
    }
  }, [currentConversation?.messages, globalMessages, isLoading]);

  // ÌõàÏäµ Îç∞Ïù¥ÌÑ∞ Î°úÎî©
  useEffect(() => {
    const loadTrainingData = async () => {
      if (!currentInterpretation?.personaId) return;

      try {
        // Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎìú (Ìï≠ÏÉÅ ÌïÑÏöî)
        const threadsResponse = await chatApi.getPersonaThreads(currentInterpretation.personaId);
        if (threadsResponse.data) {
          setThreads(threadsResponse.data);
        }

        // ÎÇòÏïÑÍ∞ÄÍ∏∞/Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞Îäî ÌïÑÏöîÌï† ÎïåÎßå Î°úÎìú (ÏÑ±Îä• Í∞úÏÑ†)
        if (selectedConversationItem === 'proceed' && !proceedContent) {
          try {
            const proceedResponse = await chatApi.generateProceed({
              persona_id: currentInterpretation.personaId,
              interpretation_content: currentInterpretation.content
            });
            if (proceedResponse.data?.proceed_content) {
              setProceedContent(proceedResponse.data.proceed_content);
            }
          } catch (error) {
            console.warn('ÎÇòÏïÑÍ∞ÄÍ∏∞ Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïã§Ìå®:', error);
          }
        }

        if (selectedConversationItem === 'sentence' && !sentenceContent) {
          try {
            const sentenceResponse = await chatApi.generateSentence({
              persona_id: currentInterpretation.personaId,
              interpretation_content: currentInterpretation.content
            });
            if (sentenceResponse.data?.sentence_content) {
              setSentenceContent(sentenceResponse.data.sentence_content);
            }
          } catch (error) {
            console.warn('Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïã§Ìå®:', error);
          }
        }
      } catch (error) {
        console.error('Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïã§Ìå®:', error);
      }
    };

    loadTrainingData();
  }, [currentInterpretation?.personaId, selectedConversationItem]);

  const handleSendMessage = async (message: string) => {
    if (selectedConversation) {
      // Í∏∞Ï°¥ ÎåÄÌôîÏóê Î©îÏãúÏßÄ Ï∂îÍ∞Ä
      return await onContinueConversation(selectedConversation, message);
    } else if (currentInterpretation) {
      // Ìï¥ÏÑù Î™®ÎìúÏóêÏÑúÎäî Ìï¥ÏÑù Í¥ÄÎ†® Î©îÏãúÏßÄ Ï†ÑÏÜ°
      return await onInterpretationMessage(message);
    } else {
      // ÏÉàÎ°úÏö¥ ÎåÄÌôî ÏãúÏûë
      return await onSendInitialMessage(message);
    }
  };

  // Ìï¥ÏÑù Î©îÏãúÏßÄ ÏàòÏ†ï Ìï∏Îì§Îü¨
  const handleEditInterpretationMessage = async (messageIndex: number, newContent: string): Promise<boolean> => {
    if (!currentInterpretation) return false;

    try {
      // Î©îÏãúÏßÄ ÏàòÏ†ï - ÏàòÏ†ï ÌõÑ ÏÉàÎ°úÏö¥ ÏùëÎãµ ÏûêÎèô ÏÉùÏÑ±
      const updatedMessages = [...currentInterpretation.messages];
      if (messageIndex >= 0 && messageIndex < updatedMessages.length) {
        // ÏàòÏ†ïÎêú Î©îÏãúÏßÄ ÏóÖÎç∞Ïù¥Ìä∏
        updatedMessages[messageIndex] = {
          ...updatedMessages[messageIndex],
          content: newContent,
          timestamp: new Date().toISOString(),
        };

        // ÏàòÏ†ïÎêú Î©îÏãúÏßÄ Ïù¥ÌõÑÏùò Î™®Îì† Î©îÏãúÏßÄÎì§ Ï†úÍ±∞
        const filteredMessages = updatedMessages.slice(0, messageIndex + 1);

        // Î∞±ÏóîÎìú APIÎ•º ÌÜµÌï¥ ÏÉàÎ°úÏö¥ AI ÏùëÎãµ ÏÉùÏÑ±
        try {
          const response = await chatApi.chatWithInterpretation(
            currentInterpretation.personaId,
            newContent
          );

          if (response.data) {
            const aiResponse = {
              role: 'assistant' as const,
              content: response.data.response,
              timestamp: response.data.timestamp,
            };

            const updatedInterpretation = {
              ...currentInterpretation,
              messages: [...filteredMessages, aiResponse],
            };

            // ÏÉÅÏúÑ Ïª¥Ìè¨ÎÑåÌä∏Ïóê ÏóÖÎç∞Ïù¥Ìä∏ ÏïåÎ¶º
            if (onUpdateInterpretation) {
              onUpdateInterpretation(updatedInterpretation);
            }

            return true;
          } else {
            // API Ìò∏Ï∂ú Ïã§Ìå® Ïãú Î°úÏª¨ÏóêÏÑú ÏûÑÏãú ÏùëÎãµ ÏÉùÏÑ±
            const aiResponse = {
              role: 'assistant' as const,
              content: `${currentInterpretation.personaName}Ïùò Í¥ÄÏ†êÏóêÏÑú ÏàòÏ†ïÎêú Î©îÏãúÏßÄÏóê ÎåÄÌï¥ ÏÉàÎ°≠Í≤å ÏùëÎãµÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.\n\n"${newContent}"Ïóê ÎåÄÌï¥ Î∂ÑÏÑùÌï¥Î≥¥Î©¥, Ïù¥Îäî Ïù¥Ï†ÑÍ≥ºÎäî Îã§Î•∏ Îß•ÎùΩÏùÑ Ï†úÏãúÌïòÍ≥† ÏûàÏäµÎãàÎã§. ÏàòÏ†ïÎêú ÎÇ¥Ïö©ÏùÑ Î∞îÌÉïÏúºÎ°ú ÏÉàÎ°úÏö¥ Ìï¥ÏÑùÍ≥º ÌÜµÏ∞∞ÏùÑ Ï†úÍ≥µÌïòÍ≤†ÏäµÎãàÎã§.`,
              timestamp: new Date().toISOString(),
            };

            const updatedInterpretation = {
              ...currentInterpretation,
              messages: [...filteredMessages, aiResponse],
            };

            if (onUpdateInterpretation) {
              onUpdateInterpretation(updatedInterpretation);
            }

            return true;
          }
        } catch (apiError) {
          console.error('Ìï¥ÏÑù API Ìò∏Ï∂ú Ïã§Ìå®:', apiError);

          // API Ìò∏Ï∂ú Ïã§Ìå® Ïãú Î°úÏª¨ÏóêÏÑú ÏûÑÏãú ÏùëÎãµ ÏÉùÏÑ±
          const aiResponse = {
            role: 'assistant' as const,
            content: `${currentInterpretation.personaName}Ïùò Í¥ÄÏ†êÏóêÏÑú ÏàòÏ†ïÎêú Î©îÏãúÏßÄÏóê ÎåÄÌï¥ ÏÉàÎ°≠Í≤å ÏùëÎãµÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.\n\n"${newContent}"Ïóê ÎåÄÌï¥ Î∂ÑÏÑùÌï¥Î≥¥Î©¥, Ïù¥Îäî Ïù¥Ï†ÑÍ≥ºÎäî Îã§Î•∏ Îß•ÎùΩÏùÑ Ï†úÏãúÌïòÍ≥† ÏûàÏäµÎãàÎã§. ÏàòÏ†ïÎêú ÎÇ¥Ïö©ÏùÑ Î∞îÌÉïÏúºÎ°ú ÏÉàÎ°úÏö¥ Ìï¥ÏÑùÍ≥º ÌÜµÏ∞∞ÏùÑ Ï†úÍ≥µÌïòÍ≤†ÏäµÎãàÎã§.`,
            timestamp: new Date().toISOString(),
          };

          const updatedInterpretation = {
            ...currentInterpretation,
            messages: [...filteredMessages, aiResponse],
          };

          if (onUpdateInterpretation) {
            onUpdateInterpretation(updatedInterpretation);
          }

          return true;
        }
      }
      return false;
    } catch (error) {
      console.error('Ìï¥ÏÑù Î©îÏãúÏßÄ ÏàòÏ†ï Ïã§Ìå®:', error);
      return false;
    }
  };

  // ÏùºÎ∞ò ÎåÄÌôî Î©îÏãúÏßÄ ÏàòÏ†ï Ìï∏Îì§Îü¨
  const handleEditConversationMessage = async (messageIndex: number, newContent: string): Promise<boolean> => {
    if (!selectedConversation || !currentConversation) return false;

    try {
      const result = await chatApi.editMessage(selectedConversation, messageIndex, newContent);

      if (result.data?.success && result.data.updated_conversation) {
        // ÏàòÏ†ïÎêú ÎåÄÌôî Ï†ïÎ≥¥Î°ú Ï¶âÏãú UI ÏóÖÎç∞Ïù¥Ìä∏
        if (onUpdateConversation) {
          onUpdateConversation(result.data.updated_conversation);
        }

        console.log('Î©îÏãúÏßÄÍ∞Ä ÏàòÏ†ïÎêòÍ≥† ÏÉàÎ°úÏö¥ AI ÏùëÎãµÏù¥ ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§:', {
          conversation: result.data.updated_conversation,
          newResponse: result.data.new_response
        });

        return true;
      } else {
        console.error('Î©îÏãúÏßÄ ÏàòÏ†ï Ïã§Ìå®:', result.error);
        return false;
      }
    } catch (error) {
      console.error('Î©îÏãúÏßÄ ÏàòÏ†ï ÏöîÏ≤≠ Ïã§Ìå®:', error);
      return false;
    }
  };

  const getHeaderTitle = () => {
    if (selectedConversation && currentConversation) {
      const persona = personas[currentConversation.persona_id];
      return `${persona?.name}Í≥ºÏùò ÎåÄÌôî`;
    }
    return 'Îã§Ï§ë ÌéòÎ•¥ÏÜåÎÇò Ï±ÑÌåÖ';
  };

  const getHeaderSubtitle = () => {
    if (selectedConversation && currentConversation) {
      return `${currentConversation.messages.length}Í∞úÏùò Î©îÏãúÏßÄ`;
    }
    return 'Î™®Îì† ÌéòÎ•¥ÏÜåÎÇòÏôÄ ÎåÄÌôîÌïòÍ±∞ÎÇò Í∞úÎ≥Ñ ÌéòÎ•¥ÏÜåÎÇòÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî';
  };

  const renderInitialResponses = () => {
    if (!globalMessages.length) return null;

    const latestMessage = globalMessages[globalMessages.length - 1];

    return (
      <div>
        {/* ÏÇ¨Ïö©Ïûê ÏßàÎ¨∏ */}
        <Message
          message={{
            role: 'user',
            content: latestMessage.user_message,
            timestamp: latestMessage.timestamp,
          }}
          personas={personas}
          showActionButtons={false}
        />

        {/* Î™®Îì† ÌéòÎ•¥ÏÜåÎÇò ÏùëÎãµ */}
        {latestMessage.responses.map((response) => (
          <Message
            key={`${latestMessage.id}_${response.persona_id}`}
            message={{
              role: 'assistant',
              content: response.content,
              timestamp: response.timestamp,
              persona_id: response.persona_id,
              persona_name: response.persona_name,
            }}
            personas={personas}
            showActionButtons={true}
          />
        ))}

        <div style={{
          margin: '20px 0',
          padding: '16px',
          backgroundColor: '#f8f8f8',
          borderRadius: '8px',
          border: 'none',
          textAlign: 'center',
        }}>
          <div style={{ fontSize: '14px', color: '#666', marginBottom: '8px' }}>
            üí° Í∞Å ÌéòÎ•¥ÏÜåÎÇòÏôÄ Í∞úÎ≥ÑÏ†ÅÏúºÎ°ú ÎåÄÌôîÎ•º Ïù¥Ïñ¥Í∞ÄÎ†§Î©¥
          </div>
          <div style={{ fontSize: '13px', color: '#888' }}>
            ÏôºÏ™Ω ÏÇ¨Ïù¥ÎìúÎ∞îÏóêÏÑú ÌéòÎ•¥ÏÜåÎÇòÎ•º ÏÑ†ÌÉùÌïòÍ±∞ÎÇò, ÌäπÏ†ï ÎåÄÌôîÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî
          </div>
        </div>
      </div>
    );
  };

  const renderConversation = () => {
    if (!currentConversation) return null;

    return currentConversation.messages.map((message, index) => (
      <Message
        key={`${currentConversation.id}_${index}`}
        message={message}
        personas={personas}
        showActionButtons={true}
        onCopy={() => {
          // Î©îÏãúÏßÄ ÎÇ¥Ïö© Î≥µÏÇ¨
          navigator.clipboard.writeText(message.content).then(() => {
            console.log('Î©îÏãúÏßÄÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
          }).catch(err => {
            console.error('Î≥µÏÇ¨ Ïã§Ìå®:', err);
          });
        }}
        onEdit={message.role === 'user' ? () => setEditingMessageIndex(index) : undefined}
        onEditSave={message.role === 'user' ? async (newContent) => {
          const success = await handleEditConversationMessage(index, newContent);
          if (success) {
            setEditingMessageIndex(null); // Ìé∏Ïßë ÏôÑÎ£å Ïãú Ìé∏Ïßë Î™®Îìú Ìï¥Ï†ú
          }
          return success;
        } : undefined}
        onEditCancel={() => setEditingMessageIndex(null)}
        isEditing={editingMessageIndex === index}
        onSunAction={() => console.log('Ìï¥ Î≤ÑÌäº - Ìñ•ÌõÑ Íµ¨ÌòÑ')}
        onPersonAction={() => console.log('ÎÇò Î≤ÑÌäº - Ìñ•ÌõÑ Íµ¨ÌòÑ')}
        onDocumentAction={() => console.log('Î¨∏ Î≤ÑÌäº - Ìñ•ÌõÑ Íµ¨ÌòÑ')}
      />
    ));
  };

  const renderEmptyState = () => (
    <EmptyState>
      <EmptyStateTitle>ÏÉàÎ°úÏö¥ ÎåÄÌôîÎ•º ÏãúÏûëÌï¥Î≥¥ÏÑ∏Ïöî!</EmptyStateTitle>
      <EmptyStateText>
        {Object.keys(personas).length > 0 ? (
          <>
            ÏïÑÎûòÏóê ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÎ©¥ {Object.keys(personas).length}Í∞úÏùò ÌéòÎ•¥ÏÜåÎÇòÍ∞Ä<br />
            Í∞ÅÍ∞Å Îã§Î•∏ Í¥ÄÏ†êÏóêÏÑú ÎãµÎ≥ÄÌï¥ÎìúÎ¶ΩÎãàÎã§.
          </>
        ) : (
          'ÌéòÎ•¥ÏÜåÎÇò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...'
        )}
      </EmptyStateText>
    </EmptyState>
  );

  // Ìï¥ÏÑù Î∑∞ Î†åÎçîÎßÅ
  const renderInterpretationView = () => {
    if (!currentInterpretation) return null;

    return (
      <InterpretationView
        interpretation={currentInterpretation.content}
        personaName={currentInterpretation.personaName}
        onSendMessage={onInterpretationMessage}
        onEditMessage={handleEditInterpretationMessage}
        isLoading={isLoading}
        messages={currentInterpretation.messages}
      />
    );
  };

  // ÎÇòÏïÑÍ∞ÄÍ∏∞ Î∑∞ Î†åÎçîÎßÅ
  const renderProceedView = () => {
    if (!currentInterpretation || !proceedContent) return null;

    return (
      <ProceedView
        personaId={currentInterpretation.personaId}
        personaName={currentInterpretation.personaName}
        proceedContent={proceedContent}
        threads={threads}
        onThreadUpdate={setThreads}
      />
    );
  };

  // Î¨∏Ïû• Î∑∞ Î†åÎçîÎßÅ
  const renderSentenceView = () => {
    if (!currentInterpretation || !sentenceContent) return null;

    return (
      <SentenceView
        personaId={currentInterpretation.personaId}
        personaName={currentInterpretation.personaName}
        sentenceContent={sentenceContent}
        threads={threads}
        onThreadUpdate={setThreads}
      />
    );
  };

  return (
    <MainArea>
      {/* Î©îÏù∏ ÏòÅÏó≠ Ìó§Îçî - ÏÇ¨Ïù¥ÎìúÎ∞îÏôÄ ÎÜíÏù¥ ÎßûÏ∂§ (80px) - Îπà Í≥µÍ∞Ñ */}
          <div style={{ 
        height: '80px',
        borderBottom: '1px solid #ddd',
        background: 'white',
          }}>
        {/* Îπà Ìó§Îçî Í≥µÍ∞Ñ */}
          </div>

      {/* ÌõàÏäµ Î™®ÎìúÏùº Îïå Ïπ¥ÌÖåÍ≥†Î¶¨ Î∑∞ ÌëúÏãú */}
      {selectedPersonaItem === 'training' ? (
        <TrainingCategoryView
          personas={personas}
          onSelectPersona={onSelectPersona || (() => {})}
          onInterpretationComplete={onInterpretationComplete}
        />
      ) : selectedPersonaItem === 'admin' ? (
        <PersonaAdminPanel 
          onClose={() => {
            // Í¥ÄÎ¶¨ Î™®Îìú Ï¢ÖÎ£å Ïãú PersonaSidebarÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
            if (onSelectPersona) {
              onSelectPersona(''); // Îπà Í∞íÏúºÎ°ú ÏÑ§Ï†ïÌïòÏó¨ PersonaSidebarÏùò selectedItemÏùÑ nullÎ°ú ÎßåÎì¶
            }
          }}
        />
      ) : currentInterpretation && selectedConversationItem === 'interpretation' ? (
        // Ìï¥ÏÑù Î™®ÎìúÏùº Îïå InterpretationView ÏÇ¨Ïö©
        renderInterpretationView()
      ) : currentInterpretation && (selectedConversationItem === 'proceed' || selectedConversationItem?.startsWith('proceed-thread-')) ? (
        // ÎÇòÏïÑÍ∞ÄÍ∏∞ Î™®ÎìúÏùº Îïå ProceedView ÏÇ¨Ïö©
        renderProceedView()
      ) : currentInterpretation && (selectedConversationItem === 'sentence' || selectedConversationItem?.startsWith('sentence-thread-')) ? (
        // Î¨∏Ïû• Î™®ÎìúÏùº Îïå SentenceView ÏÇ¨Ïö©
        renderSentenceView()
      ) : (
        <>
      <ChatContent ref={chatContentRef}>
        {/* {error && (
          <ErrorMessage onClick={onClearError}>
            {error} (ÌÅ¥Î¶≠ÌïòÏó¨ Îã´Í∏∞)
          </ErrorMessage>
        )} */}

            {!selectedConversation && !currentInterpretation && globalMessages.length === 0 && renderEmptyState()}
            {!selectedConversation && !currentInterpretation && globalMessages.length > 0 && renderInitialResponses()}
        {selectedConversation && renderConversation()}

        {isLoading && (
          selectedConversation && currentConversation ? (
            <LoadingMessage 
              personaName={personas[currentConversation.persona_id]?.name}
              personaColor={personas[currentConversation.persona_id]?.color}
            />
          ) : (
            <LoadingMessage 
              personaName="AI"
              personaColor="#666"
            />
          )
        )}
      </ChatContent>

      <MessageInput
        onSendMessage={handleSendMessage}
        disabled={isLoading || Object.keys(personas).length === 0}
        placeholder={
          selectedConversation && currentConversation
            ? `${personas[currentConversation.persona_id]?.name}ÏóêÍ≤å Î©îÏãúÏßÄÎ•º Î≥¥ÎÇ¥ÏÑ∏Ïöî...`
            : "Î™®Îì† ÌéòÎ•¥ÏÜåÎÇòÏóêÍ≤å ÏßàÎ¨∏Ìï¥Î≥¥ÏÑ∏Ïöî..."
        }
      />
        </>
      )}
    </MainArea>
  );
};

export default ChatArea;
