import React, { useState, useRef, useEffect, useImperativeHandle, forwardRef } from 'react';
import {
  InputContainer,
  InputWrapper,
  MessageInputContainer,
  MessageInput as StyledMessageInput,
  SendButton,
  SentenceModeButton,
} from '../styles/GlobalStyle';

interface MessageInputProps {
  onSendMessage: (message: string) => Promise<boolean>;
  disabled?: boolean;
  placeholder?: string;
  onToggleSentenceMode?: () => void;
  isSentenceModeActive?: boolean;
  hasSelectedSentences?: boolean;
}

export interface MessageInputRef {
  insertText: (text: string) => void;
}

const MessageInput = forwardRef<MessageInputRef, MessageInputProps>(({
  onSendMessage,
  disabled = false,
  placeholder = "메시지를 입력하세요...",
  onToggleSentenceMode,
  isSentenceModeActive = false,
  hasSelectedSentences = false,
}, ref) => {
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // ref를 통해 외부에서 텍스트 삽입 가능하도록 함
  useImperativeHandle(ref, () => ({
    insertText: (text: string) => {
      setMessage(prev => prev + text);
      // 텍스트 삽입 후 높이 조절
      setTimeout(() => {
        if (textareaRef.current) {
          textareaRef.current.style.height = 'auto';
          textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
          textareaRef.current.focus();
        }
      }, 0);
    }
  }));

  const handleSubmit = async () => {
    if (!message.trim() || disabled || isSending) return;

    setIsSending(true);
    const success = await onSendMessage(message.trim());
    
    if (success) {
      setMessage('');
      // 텍스트영역 높이 초기화
      if (textareaRef.current) {
        textareaRef.current.style.height = 'auto';
      }
    }
    
    setIsSending(false);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);
    
    // 자동 높이 조절
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  };

  // 컴포넌트가 마운트될 때 포커스
  useEffect(() => {
    if (textareaRef.current && !disabled) {
      textareaRef.current.focus();
    }
  }, [disabled]);

  const isButtonDisabled = disabled || isSending || !message.trim();

  return (
    <InputContainer>
      <InputWrapper>
        <MessageInputContainer>
          <StyledMessageInput
            ref={textareaRef}
            value={message}
            onChange={handleChange}
            onKeyPress={handleKeyPress}
            placeholder={placeholder}
            disabled={disabled || isSending}
            rows={1}
          />
          <SendButton
            onClick={handleSubmit}
            disabled={isButtonDisabled}
            title={isSending ? '전송 중...' : '전송'}
          >
            {isSending ? '⏳' : '↑'}
          </SendButton>
        </MessageInputContainer>
        <SentenceModeButton
          isActive={isSentenceModeActive}
          onClick={onToggleSentenceMode}
          title="문장 선택 모드"
        >
          문장<br/>선택
        </SentenceModeButton>
      </InputWrapper>
    </InputContainer>
  );
});

export default MessageInput;
