import React from 'react';
import {
  Sidebar,
  SidebarHeader,
  SidebarContent,
  SidebarMenuContent,
  SidebarToggleButton,
  PersonaMenuItem,
} from '../styles/GlobalStyle';

interface PersonaSidebarProps {
  selectedItem: string | null;
  onSelectItem: (itemId: string | null) => void;
  isCollapsed?: boolean;
  onToggle?: () => void;
}

const PersonaSidebar: React.FC<PersonaSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed = false,
  onToggle,
}) => {
  const menuItems = [
    { id: 'training', name: 'ÌõàÏäµ' },
    { id: 'verbalization', name: 'Ïñ∏Ïñ¥Ìôî' },
    { id: 'letter', name: 'Ìé∏ÏßÄ' },
    { id: 'storage', name: 'Ï†ÄÏû•Í≥†' },
    { id: 'recent', name: 'ÏµúÍ∑º ÏÉÅÌò∏ÏûëÏö©' },
    { id: 'admin', name: 'üõ†Ô∏è Í¥ÄÎ¶¨' },
  ];

  return (
    <Sidebar width="200px" isCollapsed={isCollapsed} variant="persona">
      {onToggle && (
        <SidebarToggleButton onClick={onToggle}>
          {isCollapsed ? '‚ñ∑' : '‚óÅ'}
        </SidebarToggleButton>
      )}
      
      {/* Ìó§Îçî ÏòÅÏó≠ (Îπà Í≥µÍ∞Ñ) */}
      <SidebarHeader isCollapsed={isCollapsed}>
        {/* Îπà Í≥µÍ∞Ñ - ÎëêÎ≤àÏß∏ ÏÇ¨Ïù¥ÎìúÎ∞îÏôÄ ÎÜíÏù¥ ÎßûÏ∂§ */}
      </SidebarHeader>

      <SidebarContent isCollapsed={isCollapsed}>
        <SidebarMenuContent variant="persona">
          {menuItems.map((item) => (
            <PersonaMenuItem
              key={item.id}
              isSelected={selectedItem === item.id}
              onClick={() => onSelectItem(item.id)}
            >
              {item.name}
            </PersonaMenuItem>
          ))}
        </SidebarMenuContent>
      </SidebarContent>
    </Sidebar>
  );
};

export default PersonaSidebar;
