import React from 'react';
import TrainingChatView from './TrainingChatView';
import { TrainingThread } from '../../shared/types';

interface InterpretationViewProps {
  interpretation: string;
  personaName: string;
  onSendMessage: (message: string) => Promise<boolean>;
  isLoading: boolean;
  messages: Array<{
    role: 'user' | 'assistant';
    content: string;
    timestamp: string;
  }>;
  personaId?: string;
  onRefreshThreads?: () => void;
  selectedThread?: TrainingThread | null;
}

const InterpretationView: React.FC<InterpretationViewProps> = (props) => {
  return (
    <TrainingChatView
      viewType="interpretation"
      {...props}
      personaId={props.personaId || ''}
      initialContent={props.interpretation}
      threads={[]} // 해석 뷰는 독립적인 스레드 목록을 갖지 않음
      onThreadUpdate={() => {}} // 임시 함수
      selectedThread={props.selectedThread || null}
    />
  );
};

export default InterpretationView;
