import React, { useState } from 'react';
import { useChat } from './hooks/useChat';
import ConversationSidebar from './components/ConversationSidebar';
import PersonaSidebar from './components/PersonaSidebar';
import ChatArea from './components/ChatArea';
import { GlobalStyle, AppContainer } from './styles/GlobalStyle';

const App: React.FC = () => {
  const [isPersonaSidebarCollapsed, setIsPersonaSidebarCollapsed] = useState(false);
  const [isConversationSidebarCollapsed, setIsConversationSidebarCollapsed] = useState(false);

  const {
    // State
    personas,
    globalMessages,
    conversations,
    selectedConversation,
    selectedPersona,
    isLoading,
    error,

    // Actions
    sendInitialMessage,
    continueConversation,
    selectConversation,
    selectPersona,
    clearError,
  } = useChat();

  const togglePersonaSidebar = () => {
    setIsPersonaSidebarCollapsed(!isPersonaSidebarCollapsed);
  };

  const toggleConversationSidebar = () => {
    setIsConversationSidebarCollapsed(!isConversationSidebarCollapsed);
  };

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        {/* 왼쪽 첫 번째 사이드바 - 페르소나 선택 */}
        <PersonaSidebar
          personas={personas}
          selectedPersona={selectedPersona}
          onSelectPersona={selectPersona}
          conversations={conversations}
          onSelectConversation={selectConversation}
          selectedConversation={selectedConversation}
          isCollapsed={isPersonaSidebarCollapsed}
          onToggle={togglePersonaSidebar}
        />

        {/* 왼쪽 두 번째 사이드바 - 대화 목록 */}
        <ConversationSidebar
          globalMessages={globalMessages}
          selectedConversation={selectedConversation}
          onSelectConversation={selectConversation}
          isCollapsed={isConversationSidebarCollapsed}
          onToggle={toggleConversationSidebar}
        />

        {/* 메인 채팅 영역 */}
        <ChatArea
          selectedConversation={selectedConversation}
          conversations={conversations}
          personas={personas}
          globalMessages={globalMessages}
          isLoading={isLoading}
          error={error}
          onSendInitialMessage={sendInitialMessage}
          onContinueConversation={continueConversation}
          onClearError={clearError}
        />
      </AppContainer>
    </>
  );
};

export default App;
