import React from 'react';
import {
  Sidebar,
  SidebarContent,
  SidebarToggleButton,
  ConversationMenuItem,
} from '../styles/GlobalStyle';
import styled from 'styled-components';

interface ConversationSidebarProps {
  selectedItem: string | null;
  onSelectItem: (itemId: string | null) => void;
  isCollapsed?: boolean;
  onToggle?: () => void;
  currentInterpretation?: {
    personaId: string;
    personaName: string;
    content: string;
    messages: Array<{
      role: 'user' | 'assistant';
      content: string;
      timestamp: string;
    }>;
  } | null;
}

const InterpretationSection = styled.div`
  padding: 16px;
  border-top: 1px solid #e0e0e0;
  background: #f8f9fa;
  max-height: 400px;
  overflow-y: auto;
`;

const InterpretationTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #333;
  margin-bottom: 12px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const InterpretationContent = styled.div`
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  padding: 16px;
  line-height: 1.5;
  color: #555;
  font-size: 13px;
  white-space: pre-line;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.05);
`;

const ConversationSidebar: React.FC<ConversationSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed = false,
  onToggle,
  currentInterpretation,
}) => {
  const menuItems = [
    { id: 'interpretation', name: 'Ìï¥ÏÑù' },
    { id: 'proceed', name: 'ÎÇòÏïÑÍ∞ÄÍ∏∞' },
    { id: 'sentence', name: 'Î¨∏Ïû•' },
    { id: 'interaction_history', name: 'ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù' },
  ];

  return (
    <Sidebar width="250px" isCollapsed={isCollapsed} variant="conversation">
      {onToggle && (
        <SidebarToggleButton onClick={onToggle}>
          {isCollapsed ? '‚ñ∑' : '‚óÅ'}
        </SidebarToggleButton>
      )}

      <SidebarContent isCollapsed={isCollapsed}>
        {menuItems.map((item) => (
          <ConversationMenuItem
            key={item.id}
            isSelected={selectedItem === item.id}
            onClick={() => onSelectItem(item.id)}
          >
            {item.name}
          </ConversationMenuItem>
        ))}

        {/* Ìï¥ÏÑù ÌÉ≠Ïù¥ ÏÑ†ÌÉùÎêòÍ≥† Ìï¥ÏÑù ÎÇ¥Ïö©Ïù¥ ÏûàÏùÑ Îïå ÌëúÏãú */}
        {selectedItem === 'interpretation' && currentInterpretation && (
          <InterpretationSection>
            <InterpretationTitle>
              üß† {currentInterpretation.personaName}Ïùò Ìï¥ÏÑù
            </InterpretationTitle>
            <InterpretationContent>
              {currentInterpretation.content}
            </InterpretationContent>
          </InterpretationSection>
        )}
      </SidebarContent>
    </Sidebar>
  );
};

export default ConversationSidebar;
