import React from 'react';
import styled from 'styled-components';

interface VaultSidebarProps {
  selectedItem: string | null;
  onSelectItem: (itemId: string | null) => void;
  isCollapsed: boolean;
  onToggle: () => void;
}

const SidebarContainer = styled.div<{ $isCollapsed: boolean }>`
  width: ${props => props.$isCollapsed ? '60px' : '280px'};
  background: linear-gradient(180deg, #f8f9fa 0%, #e9ecef 100%);
  border-right: 1px solid #dee2e6;
  display: flex;
  flex-direction: column;
  transition: width 0.3s ease;
  min-height: 100vh;
  position: relative;
`;

const Header = styled.div<{ $isCollapsed: boolean }>`
  padding: 20px;
  border-bottom: 1px solid #dee2e6;
  background: #fff;
  display: flex;
  align-items: center;
  justify-content: space-between;
  min-height: 60px;
`;

const Title = styled.h2<{ $isCollapsed: boolean }>`
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
  display: ${props => props.$isCollapsed ? 'none' : 'block'};
`;

const ToggleButton = styled.button`
  background: none;
  border: none;
  font-size: 18px;
  cursor: pointer;
  padding: 5px;
  border-radius: 4px;
  color: #6c757d;
  
  &:hover {
    background: #f8f9fa;
    color: #333;
  }
`;

const MenuList = styled.div`
  flex: 1;
  padding: 20px 0;
`;

const MenuItem = styled.div<{ $isSelected: boolean; $isCollapsed: boolean }>`
  padding: ${props => props.$isCollapsed ? '15px 20px' : '15px 20px'};
  margin: 0 10px;
  border-radius: 8px;
  cursor: pointer;
  font-size: 14px;
  font-weight: 500;
  color: ${props => props.$isSelected ? '#fff' : '#495057'};
  background: ${props => props.$isSelected ? 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)' : 'transparent'};
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 12px;
  
  &:hover {
    background: ${props => props.$isSelected ? 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)' : 'rgba(0, 123, 255, 0.1)'};
    color: ${props => props.$isSelected ? '#fff' : '#007bff'};
  }
  
  .icon {
    font-size: 16px;
    min-width: 16px;
  }
  
  .text {
    display: ${props => props.$isCollapsed ? 'none' : 'block'};
  }
`;

const VaultSidebar: React.FC<VaultSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed,
  onToggle
}) => {
  const menuItems = [
    { id: 'sentences', label: 'Î¨∏Ïû•', icon: 'üìù' },
    { id: 'memos', label: 'Î©îÎ™®', icon: 'üìã' },
    { id: 'letters', label: 'Ìé∏ÏßÄ', icon: '‚úâÔ∏è' },
    { id: 'qa-updates', label: 'QA ÏóÖÎç∞Ïù¥Ìä∏', icon: 'üîÑ' },
  ];

  return (
    <SidebarContainer $isCollapsed={isCollapsed}>
      <Header $isCollapsed={isCollapsed}>
        <Title $isCollapsed={isCollapsed}>Ï†ÄÏû•Í≥†</Title>
        <ToggleButton onClick={onToggle}>
          {isCollapsed ? '‚Üí' : '‚Üê'}
        </ToggleButton>
      </Header>
      
      <MenuList>
        {menuItems.map(item => (
          <MenuItem
            key={item.id}
            $isSelected={selectedItem === item.id}
            $isCollapsed={isCollapsed}
            onClick={() => onSelectItem(item.id)}
          >
            <span className="icon">{item.icon}</span>
            <span className="text">{item.label}</span>
          </MenuItem>
        ))}
      </MenuList>
    </SidebarContainer>
  );
};

export default VaultSidebar;
