import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// ÌÉÄÏûÖ Ï†ïÏùò
interface StimulusQuote {
  id: string;
  content: string;
  order: number;
}

interface StimulusQuoteSet {
  quotes: StimulusQuote[];
}

interface PersonaInfo {
  name: string;
  theme: string;
  summary: string;
}

interface DailyStimulusItem {
  id: string;
  date: string;
  persona_name: string;
  main_theme: string;
  quote_sets: StimulusQuoteSet[];
  summary_quote: string;
  persona_info?: PersonaInfo[];
  created_at: string;
}

interface StimulusVaultItem {
  id: string;
  original_stimulus_id: string;
  saved_content: string;
  content_type: 'quote' | 'summary' | 'theme';
  source_date: string;
  persona_name: string;
  saved_at: string;
  tags: string[];
  original_stimulus?: DailyStimulusItem;
}

interface StimulusVaultViewProps {
  personas?: Record<string, any>;
}

// StimulusViewÏôÄ ÎèôÏùºÌïú Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏Îì§
const VaultContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  position: relative;
  overflow: hidden;
`;

const VaultHeader = styled.div`
  padding: 20px 40px;
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const VaultTitle = styled.h1`
  font-size: 28px;
  font-weight: 700;
  color: #333;
  margin: 0;
`;

const VaultStats = styled.div`
  font-size: 16px;
  color: #666;
`;

const ContentArea = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
`;

const StimulusCard = styled.div`
  background: linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%);
  border-radius: 24px;
  padding: 80px 70px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
  max-width: 900px;
  width: 100%;
  position: relative;
  backdrop-filter: blur(10px);
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 50px;
  gap: 16px;
`;

const DateDisplay = styled.div`
  font-size: 18px;
  color: #666;
  font-weight: 500;
`;

const PersonaName = styled.div`
  font-size: 18px;
  color: #333;
  font-weight: 600;
`;

const MainThemeBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  margin-bottom: 30px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
`;

const MainTheme = styled.h1`
  font-size: 28px;
  font-weight: bold;
  color: #222;
  text-align: center;
  margin: 0;
  line-height: 1.4;
`;

const QuotesContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const VerticalLine = styled.div`
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: #ddd;
  transform: translateX(-50%);
  z-index: 1;
`;

const QuoteSetContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
`;

const QuoteBox = styled.div<{ $isVisible: boolean; $isFirst?: boolean; $isLast?: boolean }>`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: ${props => {
    if (props.$isFirst) return '16px 16px 0 0';
    if (props.$isLast) return '0 0 16px 16px';
    return '0';
  }};
  padding: 24px 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  transition: all 0.3s ease;
  opacity: ${props => props.$isVisible ? 1 : 0};
  transform: translateY(${props => props.$isVisible ? '0' : '20px'});
  border: 1px solid #e8e8e8;
  border-top: ${props => !props.$isFirst ? 'none' : '1px solid #e8e8e8'};
  
  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const QuoteContent = styled.div`
  font-size: 18px;
  line-height: 1.6;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const ActionButton = styled.button`
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 2px solid #ddd;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  transition: all 0.2s ease;
  font-size: 14px;
  color: #666;
  
  &:hover {
    border-color: #999;
    background: #f8f9fa;
  }
`;

const SummaryQuoteBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  margin-top: 30px;
  
  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const SummaryQuote = styled.div`
  font-size: 20px;
  font-weight: 500;
  line-height: 1.5;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const NavigationContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  display: flex;
  justify-content: space-between;
  pointer-events: none;
  z-index: 3;
`;

const NavigationButton = styled.button<{ $side: 'left' | 'right' }>`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: white;
  border: none;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  color: #666;
  opacity: 0;
  transition: all 0.3s ease;
  pointer-events: auto;
  margin: ${props => props.$side === 'left' ? '0 20px 0 -25px' : '0 -25px 0 20px'};
  
  &:hover {
    opacity: 1 !important;
    background: #f8f9fa;
    transform: scale(1.1);
  }
`;

const IndicatorContainer = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  display: flex;
  align-items: center;
  gap: 16px;
`;

const PageIndicator = styled.div`
  display: flex;
  gap: 8px;
`;

const PageDot = styled.div<{ $isActive: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  cursor: pointer;
`;

const PageNumber = styled.span<{ $isActive: boolean }>`
  font-size: 14px;
  font-weight: ${props => props.$isActive ? 'bold' : 'normal'};
  color: ${props => props.$isActive ? '#333' : '#999'};
`;

const PageBar = styled.div<{ $isActive: boolean }>`
  width: 24px;
  height: 3px;
  background: ${props => props.$isActive ? '#666' : '#ddd'};
  border-radius: 2px;
  transition: all 0.3s ease;
`;

const MenuButton = styled.button`
  width: 36px;
  height: 36px;
  border: none;
  background: #f0f0f0;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #666;
  font-size: 16px;
  
  &:hover {
    background: #e0e0e0;
  }
`;

const DropdownMenu = styled.div<{ $isVisible: boolean }>`
  position: absolute;
  top: 100%;
  right: 0;
  background: white;
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
  overflow: hidden;
  transform: translateY(${props => props.$isVisible ? '8px' : '0'});
  opacity: ${props => props.$isVisible ? 1 : 0};
  visibility: ${props => props.$isVisible ? 'visible' : 'hidden'};
  transition: all 0.2s ease;
  z-index: 10;
`;

const MenuOption = styled.div`
  padding: 12px 16px;
  background: #f8f9fa;
  color: #333;
  cursor: pointer;
  white-space: nowrap;
  border-bottom: 1px solid #eee;
  
  &:last-child {
    border-bottom: none;
  }
  
  &:hover {
    background: #e9ecef;
  }
`;

const LoadingState = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 400px;
  font-size: 18px;
  color: #666;
`;

const ErrorState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 400px;
  color: #d32f2f;
  text-align: center;
  
  button {
    margin-top: 16px;
    padding: 8px 16px;
    background: #d32f2f;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 400px;
  color: #666;
  text-align: center;
  
  .icon {
    font-size: 48px;
    margin-bottom: 16px;
  }
`;

// Î©îÏù∏ Ïª¥Ìè¨ÎÑåÌä∏
const StimulusVaultView: React.FC<StimulusVaultViewProps> = ({ personas }) => {
  const [items, setItems] = useState<StimulusVaultItem[]>([]);
  const [currentItemIndex, setCurrentItemIndex] = useState<number>(0);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [isMenuOpen, setIsMenuOpen] = useState<boolean>(false);

  // Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    loadStimulusVault();
  }, []);

  const loadStimulusVault = async () => {
    try {
      setIsLoading(true);
      setError(null);
      
      const response = await fetch('/vault/stimulus');
      const data = await response.json();
      
      if (data.success) {
        // ÏõêÎ≥∏ stimulusÍ∞Ä ÏûàÎäî Ìï≠Î™©Îì§Îßå ÌïÑÌÑ∞ÎßÅ
        const validItems = (data.items || []).filter((item: StimulusVaultItem) => item.original_stimulus);
        setItems(validItems);
      } else {
        setError('Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (err) {
      setError('ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error('ÏûêÍ∑π Ï†ÄÏû•Í≥† Î°úÎìú Ïò§Î•ò:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // ÌòÑÏû¨ Î≥¥Í≥† ÏûàÎäî ÏïÑÏù¥ÌÖú
  const currentItem = items[currentItemIndex];

  // ÌéòÏù¥ÏßÄ Î≥ÄÍ≤Ω
  const handlePageChange = (page: number) => {
    if (page >= 1 && page <= 3) {
      setCurrentPage(page);
    }
  };

  // Ï¢åÏö∞ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò (ÏïÑÏù¥ÌÖú Í∞Ñ Ïù¥Îèô)
  const handleNavigation = (direction: 'prev' | 'next') => {
    if (direction === 'prev') {
      setCurrentItemIndex(currentItemIndex === 0 ? items.length - 1 : currentItemIndex - 1);
    } else {
      setCurrentItemIndex(currentItemIndex === items.length - 1 ? 0 : currentItemIndex + 1);
    }
    setCurrentPage(1); // ÏÉàÎ°úÏö¥ ÏïÑÏù¥ÌÖúÏúºÎ°ú Ïù¥ÎèôÌï† ÎïåÎäî Ï≤´ Î≤àÏß∏ ÌéòÏù¥ÏßÄÎ°ú
  };

  // ÏïÑÏù¥ÌÖú ÏÇ≠Ï†ú
  const handleDelete = async () => {
    if (!currentItem || !window.confirm('Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;
    
    try {
      const response = await fetch(`/vault/stimulus/${currentItem.id}`, {
        method: 'DELETE'
      });
      
      const data = await response.json();
      if (data.success) {
        const newItems = items.filter(item => item.id !== currentItem.id);
        setItems(newItems);
        
        // ÏÇ≠Ï†ú ÌõÑ Ïù∏Îç±Ïä§ Ï°∞Ï†ï
        if (newItems.length === 0) {
          setCurrentItemIndex(0);
        } else if (currentItemIndex >= newItems.length) {
          setCurrentItemIndex(newItems.length - 1);
        }
        setCurrentPage(1);
      } else {
        alert('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (err) {
      console.error('ÏÇ≠Ï†ú Ïò§Î•ò:', err);
      alert('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
    
    setIsMenuOpen(false);
  };

  // ÏÉÅÌò∏ÏûëÏö© Î≤ÑÌäº ÌÅ¥Î¶≠
  const handleInteraction = (quoteId: string, quoteContent: string) => {
    navigator.clipboard.writeText(quoteContent);
    alert('ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
  };

  // Ïª®ÌÖêÏ∏† Î†åÎçîÎßÅ
  const renderContent = () => {
    if (isLoading) {
      return <LoadingState>Ï†ÄÏû•Îêú ÏûêÍ∑πÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</LoadingState>;
    }
    
    if (error) {
      return (
        <ErrorState>
          <div>{error}</div>
          <button onClick={loadStimulusVault}>Îã§Ïãú ÏãúÎèÑ</button>
        </ErrorState>
      );
    }
    
    if (items.length === 0) {
      return (
        <EmptyState>
          <div className="icon">‚ö°</div>
          <div>Ï†ÄÏû•Îêú ÏûêÍ∑πÏù¥ ÏóÜÏäµÎãàÎã§</div>
          <div style={{ fontSize: '14px', marginTop: '8px', opacity: 0.7 }}>
            ÏûêÍ∑π ÌéòÏù¥ÏßÄÏóêÏÑú Í≤©Ïñ∏ÏùÑ Ï†ÄÏû•Ìï¥Î≥¥ÏÑ∏Ïöî
          </div>
        </EmptyState>
      );
    }

    if (!currentItem || !currentItem.original_stimulus) {
      return <LoadingState>ÏûêÍ∑π Îç∞Ïù¥ÌÑ∞Î•º Ï≤òÎ¶¨ÌïòÎäî Ï§ë...</LoadingState>;
    }

    const stimulus = currentItem.original_stimulus;

    // ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Ïã¨Î¶¨ÌïôÏûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const getCurrentPersonaInfo = () => {
      if (stimulus.persona_info && stimulus.persona_info[currentPage - 1]) {
        return stimulus.persona_info[currentPage - 1];
      }
      return null;
    };

    const currentPersona = getCurrentPersonaInfo();

    return (
      <StimulusCard>
        <Header>
          <DateDisplay>{stimulus.date} Ï†ÄÏû•Îê®</DateDisplay>
          <PersonaName>
            {currentPersona ? currentPersona.name : stimulus.persona_name}
          </PersonaName>
        </Header>

        <MainThemeBox>
          <MainTheme>
            {currentPersona ? currentPersona.theme : stimulus.main_theme}
          </MainTheme>
          <ActionButton className="action-button" onClick={() => handleInteraction('theme', currentPersona ? currentPersona.theme : stimulus.main_theme)}>
            ‚ãØ
          </ActionButton>
        </MainThemeBox>

        <QuotesContainer>
          <VerticalLine />
          
          {/* ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏùò Í≤©Ïñ∏ ÏÑ∏Ìä∏ ÌëúÏãú */}
          {currentPage <= 3 && stimulus.quote_sets && stimulus.quote_sets[currentPage - 1] && (
            <QuoteSetContainer>
              {stimulus.quote_sets[currentPage - 1].quotes.map((quote, index) => (
                <QuoteBox 
                  key={quote.id}
                  $isVisible={true}
                  $isFirst={index === 0}
                  $isLast={index === stimulus.quote_sets[currentPage - 1].quotes.length - 1}
                >
                  <QuoteContent>{quote.content}</QuoteContent>
                  <ActionButton 
                    className="action-button" 
                    onClick={() => handleInteraction(quote.id, quote.content)}
                  >
                    ‚ãØ
                  </ActionButton>
                </QuoteBox>
              ))}
            </QuoteSetContainer>
          )}
        </QuotesContainer>

        <SummaryQuoteBox>
          <SummaryQuote>
            {currentPersona ? currentPersona.summary : stimulus.summary_quote}
          </SummaryQuote>
          <ActionButton className="action-button" onClick={() => handleInteraction('summary', currentPersona ? currentPersona.summary : stimulus.summary_quote)}>
            ‚ãØ
          </ActionButton>
        </SummaryQuoteBox>

        {/* ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î≤ÑÌäº (ÏïÑÏù¥ÌÖú Í∞Ñ Ïù¥Îèô) */}
        {items.length > 1 && (
          <NavigationContainer 
            onMouseEnter={(e) => {
              const buttons = e.currentTarget.querySelectorAll('button');
              buttons.forEach(btn => (btn as HTMLElement).style.opacity = '0.7');
            }}
            onMouseLeave={(e) => {
              const buttons = e.currentTarget.querySelectorAll('button');
              buttons.forEach(btn => (btn as HTMLElement).style.opacity = '0');
            }}
          >
            <NavigationButton 
              $side="left" 
              onClick={() => handleNavigation('prev')}
            >
              ‚Üê
            </NavigationButton>
            <NavigationButton 
              $side="right" 
              onClick={() => handleNavigation('next')}
            >
              ‚Üí
            </NavigationButton>
          </NavigationContainer>
        )}

