import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { chatApi } from '../api/chatApi';
import { TrainingThread, InteractionRecord, Persona } from '../types';

interface RecentInteractionsViewProps {
  personas: Record<string, Persona>;
  onSelectPersona: (personaId: string) => void;
  onThreadSelect: (thread: TrainingThread, personaId: string) => void;
  onClearChat: () => void;
}

const Container = styled.div<{ height: number }>`
  display: flex;
  flex-direction: column;
  height: ${props => props.height}px;
  background: white;
  border-bottom: 1px solid #e0e0e0;
  position: relative;
`;

const Header = styled.div`
  padding: 20px;
  border-bottom: 1px solid #e0e0e0;
  background: linear-gradient(135deg, #f8f9fa 0%, #f1f3f4 100%);
  cursor: pointer;
  
  &:hover {
    background: linear-gradient(135deg, #f1f3f4 0%, #e9ecef 100%);
  }
`;

const Title = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #333;
  margin: 0 0 8px 0;
`;

const Subtitle = styled.p`
  font-size: 14px;
  color: #666;
  margin: 0;
`;

const InteractionsList = styled.div<{ isExpanded: boolean }>`
  flex: 1;
  overflow-y: auto;
  padding: ${props => props.isExpanded ? '20px' : '0'};
  display: ${props => props.isExpanded ? 'flex' : 'none'};
  flex-direction: column;
  gap: 16px;
`;

const InteractionCard = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 12px;
  background: white;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
  transition: all 0.2s ease;
  
  &:hover {
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.12);
    transform: translateY(-2px);
  }
`;

const ThreadsContainer = styled.div`
  padding: 16px 20px;
`;

const ThreadItem = styled.div`
  padding: 12px 16px;
  margin-bottom: 8px;
  border: 1px solid #f0f0f0;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover {
    background: #f8f9fa;
    border-color: #6c757d;
  }
  
  &:last-child {
    margin-bottom: 0;
  }
`;

const ThreadHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
`;

const ThreadType = styled.span`
  display: inline-block;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 10px;
  font-weight: 600;
  text-transform: uppercase;
  background-color: #6c757d;
  color: white;
`;

const ThreadTime = styled.span`
  font-size: 12px;
  color: #666;
`;

const ThreadContent = styled.div`
  font-size: 14px;
  color: #333;
  line-height: 1.4;
`;

const EmptyState = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #999;
  font-size: 14px;
  text-align: center;
  padding: 40px 20px;
`;

const Resizer = styled.div`
  height: 6px;
  background: #f0f0f0;
  cursor: row-resize;
  border-top: 1px solid #e0e0e0;
  transition: background-color 0.2s ease;
  
  &:hover {
    background: #ddd;
  }
  
  &:active {
    background: #ccc;
  }
`;

const PersonaSection = styled.div`
  border-bottom: 1px solid #f0f0f0;
  
  &:last-child {
    border-bottom: none;
  }
`;

const PersonaHeader = styled.div`
  padding: 16px 20px;
  background: #f8f9fa;
  cursor: pointer;
  border-bottom: 1px solid #e0e0e0;
  transition: background-color 0.2s ease;
  
  &:hover {
    background: #f1f3f4;
  }
`;

const PersonaName = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #333;
  margin: 0 0 4px 0;
`;

const PersonaInfo = styled.div`
  font-size: 12px;
  color: #666;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const RecentInteractionsView: React.FC<RecentInteractionsViewProps> = ({
  personas,
  onSelectPersona,
  onThreadSelect,
  onClearChat,
}) => {
  const [interactionRecords, setInteractionRecords] = useState<InteractionRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [isExpanded, setIsExpanded] = useState(false);
  const [height, setHeight] = useState(200);
  const [expandedPersonas, setExpandedPersonas] = useState<Set<string>>(new Set());
  const [isResizing, setIsResizing] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const loadInteractionRecords = async () => {
      try {
        setLoading(true);
        const recordsResponse = await chatApi.getAllInteractionRecords();
        if (recordsResponse.data) {
          setInteractionRecords(recordsResponse.data);
        }
      } catch (error) {
        console.error('ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù Î°úÎìú Ïã§Ìå®:', error);
      } finally {
        setLoading(false);
      }
    };

    loadInteractionRecords();
  }, []);

  const handleHeaderClick = () => {
    setIsExpanded(!isExpanded);
    if (!isExpanded) {
      onClearChat();
    }
  };

  const handlePersonaToggle = (personaId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    const newExpanded = new Set(expandedPersonas);
    if (newExpanded.has(personaId)) {
      newExpanded.delete(personaId);
    } else {
      newExpanded.add(personaId);
    }
    setExpandedPersonas(newExpanded);
  };

  const handleThreadClick = (thread: TrainingThread, personaId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    onThreadSelect(thread, personaId);
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsResizing(true);
    e.preventDefault();
  };

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      if (!isResizing || !containerRef.current) return;
      
      const containerRect = containerRef.current.getBoundingClientRect();
      const newHeight = e.clientY - containerRect.top;
      const minHeight = 100;
      const maxHeight = window.innerHeight * 0.6;
      
      setHeight(Math.min(Math.max(newHeight, minHeight), maxHeight));
    };

    const handleMouseUp = () => {
      setIsResizing(false);
    };

    if (isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isResizing]);

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleDateString('ko-KR', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getThreadTypeLabel = (type: string) => {
    switch (type) {
      case 'interpretation': return 'Ìï¥ÏÑù';
      case 'proceed': return 'ÎÇòÏïÑÍ∞ÄÍ∏∞';
      case 'sentence': return 'Î¨∏Ïû•';
      default: return type;
    }
  };

  const getThreadContent = (thread: TrainingThread) => {
    if (thread.messages && thread.messages.length > 0) {
      const firstMessage = thread.messages[0];
      return firstMessage.content.length > 100 
        ? firstMessage.content.substring(0, 100) + '...' 
        : firstMessage.content;
    }
    return thread.content?.length > 100 
      ? thread.content.substring(0, 100) + '...' 
      : thread.content || 'ÎÇ¥Ïö© ÏóÜÏùå';
  };

  // ÌéòÎ•¥ÏÜåÎÇòÎ≥ÑÎ°ú Í∑∏Î£πÌôî (ÏµúÏã† Ïä§Î†àÎìú Í∏∞Ï§ÄÏúºÎ°ú Ï†ïÎ†¨)
  const groupedByPersona = interactionRecords.sort((a, b) => 
    new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
  );

  if (loading) {
    return (
      <Container height={height} ref={containerRef}>
        <Header onClick={handleHeaderClick}>
          <Title>ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù</Title>
          <Subtitle>Î°úÎî© Ï§ë...</Subtitle>
        </Header>
        <InteractionsList isExpanded={isExpanded}>
          <EmptyState>
            <div style={{ fontSize: '24px', marginBottom: '8px' }}>‚è≥</div>
            <div>ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</div>
          </EmptyState>
        </InteractionsList>
        {isExpanded && <Resizer onMouseDown={handleMouseDown} />}
      </Container>
    );
  }

  return (
    <Container height={height} ref={containerRef}>
      <Header onClick={handleHeaderClick}>
        <Title>ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù</Title>
        <Subtitle>
          {isExpanded ? 'ÌÅ¥Î¶≠ÌïòÏó¨ Ï†ëÍ∏∞' : 'ÌÅ¥Î¶≠ÌïòÏó¨ ÌéºÏπòÍ∏∞'}
        </Subtitle>
      </Header>

      <InteractionsList isExpanded={isExpanded}>
        {groupedByPersona.length === 0 ? (
          <EmptyState>
            <div style={{ fontSize: '24px', marginBottom: '8px' }}>üìä</div>
            <div>ÏïÑÏßÅ ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§</div>
            <div style={{ fontSize: '12px', marginTop: '4px' }}>
              ÌéòÎ•¥ÏÜåÎÇòÏôÄ ÎåÄÌôîÌïòÍ≥† Ìï¥ÏÑùÏùÑ ÏÉùÏÑ±ÌïòÎ©¥ Í∏∞Î°ùÏù¥ ÎßåÎì§Ïñ¥ÏßëÎãàÎã§
            </div>
          </EmptyState>
        ) : (
          <InteractionCard>
            {groupedByPersona.map((record) => (
              <PersonaSection key={record.id}>
                <PersonaHeader onClick={(e) => handlePersonaToggle(record.persona_id, e)}>
                  <PersonaName>{record.persona_name}</PersonaName>
                  <PersonaInfo>
                    <span>{record.threads.length}Í∞ú Ïä§Î†àÎìú</span>
                    <span>{formatTime(record.updated_at)}</span>
                  </PersonaInfo>
                </PersonaHeader>
                
                {expandedPersonas.has(record.persona_id) && (
                  <ThreadsContainer>
                    {record.threads.map((thread) => (
                      <ThreadItem
                        key={thread.id}
                        onClick={(e) => handleThreadClick(thread, record.persona_id, e)}
                      >
                        <ThreadHeader>
                          <ThreadType>
                            {getThreadTypeLabel(thread.thread_type)}
                          </ThreadType>
                          <ThreadTime>{formatTime(thread.created_at)}</ThreadTime>
                        </ThreadHeader>
                        <ThreadContent>{getThreadContent(thread)}</ThreadContent>
                      </ThreadItem>
                    ))}
                  </ThreadsContainer>
                )}
              </PersonaSection>
            ))}
          </InteractionCard>
        )}
      </InteractionsList>
      
      {isExpanded && <Resizer onMouseDown={handleMouseDown} />}
    </Container>
  );
};

export default RecentInteractionsView;
