import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { TrainingThread } from '../../shared/types';
import { chatApi } from '../../shared/api/chatApi';
import MessageInput, { MessageInputRef } from '../../shared/components/MessageInput';
import LoadingMessage from '../../shared/components/LoadingMessage';

interface VerbalizationViewProps {
  selectedThread: TrainingThread | null;
  threads: TrainingThread[];
  onThreadUpdate: () => void;
  onCreateNewThread: () => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const HeaderSection = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid #e0e0e0;
  background: #f8f9fa;
`;

const HeaderTitle = styled.h2`
  font-size: 24px;
  font-weight: 700;
  color: #343a40;
  margin: 0;
`;

const NewThreadButton = styled.button`
  background: #007bff;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: #0056b3;
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const ChatSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const ChatMessages = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const ChatInputSection = styled.div`
  padding: 16px 20px;
  border-top: none;
  background: #ffffff;
`;

const EmptyChat = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #999;
  font-size: 14px;
  text-align: center;
  line-height: 1.6;
`;

const EmptyIcon = styled.div`
  font-size: 48px;
  margin-bottom: 16px;
`;

const MessageContainer = styled.div<{ isUser: boolean }>`
  display: flex;
  justify-content: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  margin-bottom: 12px;
`;

const MessageBubble = styled.div<{ isUser: boolean }>`
  max-width: 70%;
  padding: 12px 16px;
  border-radius: 18px;
  background: ${props => props.isUser ? '#007bff' : '#f1f3f4'};
  color: ${props => props.isUser ? 'white' : '#333'};
  word-wrap: break-word;
  white-space: pre-wrap;
  line-height: 1.4;
  font-size: 14px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
`;

const MessageTime = styled.div`
  font-size: 12px;
  color: #999;
  margin-top: 4px;
  text-align: center;
`;

const VerbalizationView: React.FC<VerbalizationViewProps> = ({
  selectedThread,
  threads,
  onThreadUpdate,
  onCreateNewThread
}) => {
  const [isLoading, setIsLoading] = useState(false);
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);

  // 스크롤을 맨 아래로 이동
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [selectedThread?.messages]);

  // 메시지 전송 처리
  const handleSendMessage = async (message: string): Promise<boolean> => {
    if (!message.trim() || isLoading) return false;

    setIsLoading(true);

    try {
      console.log('=== 언어화 메시지 전송 ===');
      console.log('selectedThread:', selectedThread);
      console.log('message:', message);

      const response = await chatApi.chatWithVerbalization(
        message,
        selectedThread?.id
      );

      console.log('API 응답 전체:', response);
      console.log('응답 데이터:', response.data);
      console.log('응답 에러:', response.error);

      if (response.data) {
        console.log('메시지 전송 성공');
        console.log('응답 내용:', response.data.response);
        onThreadUpdate();
        return true;
      } else {
        console.error('메시지 전송 실패:', response.error);
        alert(`언어화 채팅 실패: ${response.error}`);
        return false;
      }
    } catch (error) {
      console.error('메시지 전송 오류:', error);
      alert(`네트워크 오류: ${error}`);
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  // 시간 포맷팅
  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // 새 스레드 생성 처리
  const handleCreateNewThread = async () => {
    try {
      const response = await chatApi.createNewVerbalizationThread();
      if (response.data) {
        onCreateNewThread();
        onThreadUpdate();
      }
    } catch (error) {
      console.error('새 스레드 생성 오류:', error);
    }
  };

  return (
    <Container>
      {/* 헤더 */}
      <HeaderSection>
        <HeaderTitle>언어화</HeaderTitle>
        <NewThreadButton onClick={handleCreateNewThread}>
          새 대화 시작
        </NewThreadButton>
      </HeaderSection>

      {/* 채팅 영역 */}
      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {!selectedThread || selectedThread.messages.length === 0 ? (
            <EmptyChat>
              <EmptyIcon>💭</EmptyIcon>
              <div>
                <strong>언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써<br />
                그것을 인식하고 만들어 가는 것입니다.</strong>
                <br /><br />
                자유롭게 당신의 생각을 말로 내뱉으세요!
                <br /><br />
                어떤 것이든 좋습니다. 지금 느끼고 있는 감정이나 생각을 편안하게 말씀해 주세요.
              </div>
            </EmptyChat>
          ) : (
            selectedThread.messages.map((message, index) => (
              <MessageContainer key={index} isUser={message.role === 'user'}>
                <div>
                  <MessageBubble isUser={message.role === 'user'}>
                    {message.content}
                  </MessageBubble>
                  <MessageTime>
                    {formatTime(message.timestamp)}
                  </MessageTime>
                </div>
              </MessageContainer>
            ))
          )}
          
          {/* 로딩 메시지 */}
          {isLoading && (
            <MessageContainer isUser={false}>
              <LoadingMessage />
            </MessageContainer>
          )}
        </ChatMessages>

        {/* 입력창 */}
        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={handleSendMessage}
            disabled={isLoading}
            placeholder="언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써 그것을 인식하고 만들어 가는 것입니다. 자유롭게 당신의 생각을 말로 내뱉으세요!"
          />
        </ChatInputSection>
      </ChatSection>
    </Container>
  );
};

export default VerbalizationView;
