import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// 타입 정의
interface StimulusQuote {
  id: string;
  content: string;
  order: number;
}

interface StimulusQuoteSet {
  quotes: StimulusQuote[]; // 3개씩 묶인 격언들
}

interface DailyStimulusItem {
  id: string;
  date: string;
  persona_name: string;
  main_theme: string;
  quote_sets: StimulusQuoteSet[]; // 3개의 세트 (총 9개 격언)
  summary_quote: string;
  created_at: string;
}

interface StimulusMentor {
  id: string;
  name: string;
  style: string;
  themes: string[];
  quote_style: string;
}

interface StimulusViewProps {
  personas?: Record<string, any>;
}

// 스타일 컴포넌트들
const StimulusContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  position: relative;
  overflow: hidden;
`;

const ContentArea = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
`;

const StimulusCard = styled.div`
  background: linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%);
  border-radius: 24px;
  padding: 80px 70px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
  max-width: 900px;
  width: 100%;
  position: relative;
  backdrop-filter: blur(10px);
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 50px;
  gap: 16px;
`;

const DateDisplay = styled.div`
  font-size: 18px;
  color: #666;
  font-weight: 500;
`;

const PersonaName = styled.div`
  font-size: 18px;
  color: #333;
  font-weight: 600;
`;

const MainThemeBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  margin-bottom: 30px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
`;

const MainTheme = styled.h1`
  font-size: 28px;
  font-weight: bold;
  color: #222;
  text-align: center;
  margin: 0;
  line-height: 1.4;
`;

const QuotesContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const VerticalLine = styled.div`
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: #ddd;
  transform: translateX(-50%);
  z-index: 1;
`;

const QuoteSetContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
`;

const QuoteBox = styled.div<{ $isVisible: boolean; $isFirst?: boolean; $isLast?: boolean }>`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: ${props => {
    if (props.$isFirst) return '16px 16px 0 0';
    if (props.$isLast) return '0 0 16px 16px';
    return '0';
  }};
  padding: 24px 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  transition: all 0.3s ease;
  opacity: ${props => props.$isVisible ? 1 : 0};
  transform: translateY(${props => props.$isVisible ? '0' : '20px'});
  border: 1px solid #e8e8e8;
  border-top: ${props => !props.$isFirst ? 'none' : '1px solid #e8e8e8'};
  
  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const QuoteContent = styled.div`
  font-size: 18px;
  line-height: 1.6;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const ActionButton = styled.button`
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 2px solid #ddd;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  transition: all 0.2s ease;
  font-size: 14px;
  color: #666;
  
  &:hover {
    border-color: #999;
    background: #f8f9fa;
  }
`;

const SummaryQuoteBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  margin-top: 30px;
  
  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const SummaryQuote = styled.div`
  font-size: 20px;
  font-weight: 500;
  line-height: 1.5;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const NavigationContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  display: flex;
  justify-content: space-between;
  pointer-events: none;
  z-index: 3;
`;

const NavigationButton = styled.button<{ $side: 'left' | 'right' }>`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: white;
  border: none;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  color: #666;
  opacity: 0;
  transition: all 0.3s ease;
  pointer-events: auto;
  margin: ${props => props.$side === 'left' ? '0 20px 0 -25px' : '0 -25px 0 20px'};
  
  &:hover {
    opacity: 1 !important;
    background: #f8f9fa;
    transform: scale(1.1);
  }
`;

const IndicatorContainer = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  display: flex;
  align-items: center;
  gap: 16px;
`;

const PageIndicator = styled.div`
  display: flex;
  gap: 8px;
`;

const PageDot = styled.div<{ $isActive: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  cursor: pointer;
`;

const PageNumber = styled.span<{ $isActive: boolean }>`
  font-size: 14px;
  font-weight: ${props => props.$isActive ? 'bold' : 'normal'};
  color: ${props => props.$isActive ? '#333' : '#999'};
`;

const PageBar = styled.div<{ $isActive: boolean }>`
  width: 24px;
  height: 3px;
  background: ${props => props.$isActive ? '#666' : '#ddd'};
  border-radius: 2px;
  transition: all 0.3s ease;
`;

const MenuButton = styled.button`
  width: 36px;
  height: 36px;
  border: none;
  background: #f0f0f0;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #666;
  font-size: 16px;
  
  &:hover {
    background: #e0e0e0;
  }
`;

const DropdownMenu = styled.div<{ $isVisible: boolean }>`
  position: absolute;
  top: 100%;
  right: 0;
  background: white;
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
  overflow: hidden;
  transform: translateY(${props => props.$isVisible ? '8px' : '0'});
  opacity: ${props => props.$isVisible ? 1 : 0};
  visibility: ${props => props.$isVisible ? 'visible' : 'hidden'};
  transition: all 0.2s ease;
  z-index: 10;
`;

const MenuOption = styled.div`
  padding: 12px 16px;
  background: #f8f9fa;
  color: #333;
  cursor: pointer;
  white-space: nowrap;
  border-bottom: 1px solid #eee;
  
  &:last-child {
    border-bottom: none;
  }
  
  &:hover {
    background: #e9ecef;
  }
`;

const MentorSelectorContainer = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  display: flex;
  align-items: center;
  gap: 12px;
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 8px 16px;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
`;

const MentorLabel = styled.span`
  font-size: 14px;
  color: #666;
  font-weight: 500;
`;

const MentorSelect = styled.select`
  border: none;
  background: transparent;
  font-size: 14px;
  color: #333;
  font-weight: 600;
  cursor: pointer;
  outline: none;
  
  &:focus {
    outline: none;
  }
`;

const RefreshButton = styled.button`
  width: 24px;
  height: 24px;
  border: none;
  background: #f0f0f0;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #666;
  font-size: 12px;
  
  &:hover {
    background: #e0e0e0;
  }
`;

const LoadingState = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 400px;
  font-size: 18px;
  color: #666;
`;

const ErrorState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 400px;
  color: #d32f2f;
  text-align: center;
  
  button {
    margin-top: 16px;
    padding: 8px 16px;
    background: #d32f2f;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }
`;

// 메인 컴포넌트
const StimulusView: React.FC<StimulusViewProps> = ({ personas }) => {
  const [currentStimulus, setCurrentStimulus] = useState<DailyStimulusItem | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [isMenuOpen, setIsMenuOpen] = useState<boolean>(false);
  const [availableMentors, setAvailableMentors] = useState<StimulusMentor[]>([]);
  const [selectedMentorId, setSelectedMentorId] = useState<string>('auto');

  // 초기 로드
  useEffect(() => {
    loadMentors();
    loadTodayStimulus();
  }, []);

  const loadMentors = async () => {
    try {
      const response = await fetch('/stimulus/mentors');
      const data = await response.json();
      
      if (data.success && data.mentors) {
        setAvailableMentors(data.mentors);
      }
    } catch (err) {
      console.error('멘토 목록 로드 오류:', err);
    }
  };

  const loadTodayStimulus = async () => {
    try {
      setIsLoading(true);
      setError(null);
      
      const response = await fetch('/stimulus/today');
      const data = await response.json();
      
      if (data.success && data.stimulus) {
        setCurrentStimulus(data.stimulus);
      } else {
        setError('자극을 불러오는데 실패했습니다.');
      }
    } catch (err) {
      setError('네트워크 오류가 발생했습니다.');
      console.error('자극 로드 오류:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // 페이지 변경
  const handlePageChange = (page: number) => {
    if (page >= 1 && page <= 3) {
      setCurrentPage(page);
    }
  };

  // 좌우 네비게이션
  const handleNavigation = (direction: 'prev' | 'next') => {
    if (direction === 'prev') {
      setCurrentPage(currentPage === 1 ? 3 : currentPage - 1);
    } else {
      setCurrentPage(currentPage === 3 ? 1 : currentPage + 1);
    }
  };

  // 상호작용 버튼 클릭
  const handleInteraction = (quoteId: string, quoteContent: string) => {
    alert(`상호작용 기능 (추후 구현)\n격언: ${quoteContent}`);
  };

  // 멘토 선택 핸들러
  const handleMentorChange = (mentorId: string) => {
    setSelectedMentorId(mentorId);
  };

  // 새로고침 (멘토별 자극 생성)
  const handleRefresh = async () => {
    try {
      setIsLoading(true);
      setError(null);
      
      let response;
      if (selectedMentorId === 'auto') {
        response = await fetch('/stimulus/today');
      } else {
        response = await fetch('/stimulus/generate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ mentor_id: selectedMentorId })
        });
      }
      
      const data = await response.json();
      
      if (data.success && data.stimulus) {
        setCurrentStimulus(data.stimulus);
        setCurrentPage(1); // 첫 페이지로 리셋
      } else {
        setError('자극을 불러오는데 실패했습니다.');
      }
    } catch (err) {
      setError('네트워크 오류가 발생했습니다.');
      console.error('자극 새로고침 오류:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // 저장하기
  const handleSave = async () => {
    if (!currentStimulus) return;
    
    try {
      const response = await fetch('/stimulus/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          stimulus_id: currentStimulus.id,
          save_type: 'all'
        })
      });
      
      const data = await response.json();
      if (data.success) {
        alert('저장되었습니다!');
      }
    } catch (err) {
      console.error('저장 오류:', err);
      alert('저장에 실패했습니다.');
    }
    
    setIsMenuOpen(false);
  };

  // 컨텐츠 렌더링
  const renderContent = () => {
    if (isLoading) {
      return <LoadingState>오늘의 자극을 불러오는 중...</LoadingState>;
    }
    
    if (error) {
      return (
        <ErrorState>
          <div>{error}</div>
          <button onClick={loadTodayStimulus}>다시 시도</button>
        </ErrorState>
      );
    }
    
    if (!currentStimulus) {
      return <LoadingState>자극이 없습니다.</LoadingState>;
    }

    return (
      <StimulusCard>
        <Header>
          <DateDisplay>{currentStimulus.date}</DateDisplay>
          <PersonaName>{currentStimulus.persona_name}</PersonaName>
        </Header>

        <MainThemeBox>
          <MainTheme>{currentStimulus.main_theme}</MainTheme>
          <ActionButton className="action-button" onClick={() => handleInteraction('theme', currentStimulus.main_theme)}>
            ⋯
          </ActionButton>
        </MainThemeBox>

        <QuotesContainer>
          <VerticalLine />
          
          {/* 현재 페이지의 격언 세트 표시 */}
          {currentPage <= 3 && currentStimulus.quote_sets && currentStimulus.quote_sets[currentPage - 1] && (
            <QuoteSetContainer>
              {currentStimulus.quote_sets[currentPage - 1].quotes.map((quote, index) => (
                <QuoteBox 
                  key={quote.id}
                  $isVisible={true}
                  $isFirst={index === 0}
                  $isLast={index === currentStimulus.quote_sets[currentPage - 1].quotes.length - 1}
                >
                  <QuoteContent>{quote.content}</QuoteContent>
                  <ActionButton 
                    className="action-button" 
                    onClick={() => handleInteraction(quote.id, quote.content)}
                  >
                    ⋯
                  </ActionButton>
                </QuoteBox>
              ))}
            </QuoteSetContainer>
          )}
          
          {/* 호환성을 위한 fallback - 오래된 데이터 구조 처리 */}
          {(!currentStimulus.quote_sets && (currentStimulus as any).quotes) && (
            <QuoteSetContainer>
              <QuoteBox $isVisible={true} $isFirst={true} $isLast={true}>
                <QuoteContent>{(currentStimulus as any).quotes[currentPage - 1]?.content}</QuoteContent>
                <ActionButton 
                  className="action-button" 
                  onClick={() => handleInteraction('legacy', (currentStimulus as any).quotes[currentPage - 1]?.content || '')}
                >
                  ⋯
                </ActionButton>
              </QuoteBox>
            </QuoteSetContainer>
          )}
        </QuotesContainer>

        <SummaryQuoteBox>
          <SummaryQuote>{currentStimulus.summary_quote}</SummaryQuote>
          <ActionButton className="action-button" onClick={() => handleInteraction('summary', currentStimulus.summary_quote)}>
            ⋯
          </ActionButton>
        </SummaryQuoteBox>

        {/* 네비게이션 버튼 */}
        <NavigationContainer 
          onMouseEnter={(e) => {
            const buttons = e.currentTarget.querySelectorAll('button');
            buttons.forEach(btn => btn.style.opacity = '0.7');
          }}
          onMouseLeave={(e) => {
            const buttons = e.currentTarget.querySelectorAll('button');
            buttons.forEach(btn => btn.style.opacity = '0');
          }}
        >
          <NavigationButton 
            $side="left" 
            onClick={() => handleNavigation('prev')}
          >
            ←
          </NavigationButton>
          <NavigationButton 
            $side="right" 
            onClick={() => handleNavigation('next')}
          >
            →
          </NavigationButton>
        </NavigationContainer>

        {/* 페이지 인디케이터 및 메뉴 */}
        <IndicatorContainer>
          <PageIndicator>
            {[1, 2, 3].map(page => (
              <PageDot 
                key={page} 
                $isActive={currentPage === page}
                onClick={() => handlePageChange(page)}
              >
                <PageNumber $isActive={currentPage === page}>{page}</PageNumber>
                <PageBar $isActive={currentPage === page} />
              </PageDot>
            ))}
          </PageIndicator>
          
          <div style={{ position: 'relative' }}>
            <MenuButton onClick={() => setIsMenuOpen(!isMenuOpen)}>
              📄
            </MenuButton>
            <DropdownMenu $isVisible={isMenuOpen}>
              <MenuOption onClick={() => setIsMenuOpen(false)}>
                이 자극의 문장들에 대하여
              </MenuOption>
              <MenuOption onClick={handleSave}>
                저장하기
              </MenuOption>
            </DropdownMenu>
          </div>
        </IndicatorContainer>
      </StimulusCard>
    );
  };

  return (
    <StimulusContainer>
      <MentorSelectorContainer>
        <MentorLabel>멘토:</MentorLabel>
        <MentorSelect 
          value={selectedMentorId} 
          onChange={(e) => handleMentorChange(e.target.value)}
        >
          <option value="auto">자동 선택</option>
          {availableMentors.map(mentor => (
            <option key={mentor.id} value={mentor.id}>
              {mentor.name}
            </option>
          ))}
        </MentorSelect>
        <RefreshButton onClick={handleRefresh} title="새로고침">
          ↻
        </RefreshButton>
      </MentorSelectorContainer>
      <ContentArea>
        {renderContent()}
      </ContentArea>
    </StimulusContainer>
  );
};

export default StimulusView;
