`;

export const SidebarContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
`;

export const MainArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  background-color: #ffffff;
`;

export const ChatHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid #e1e5e9;
  background-color: #f8f9fa;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const ChatHeaderTitle = styled.h1`
  font-size: 24px;
  font-weight: 600;
  color: #333;
`;

export const ChatContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

export const MessageContainer = styled.div<{ isUser?: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  margin-bottom: 16px;
`;

export const MessageBubble = styled.div<{ isUser?: boolean; personaColor?: string }>`
  max-width: 70%;
  padding: 16px 20px;
  border-radius: 20px;
  background-color: ${props => 
    props.isUser 
      ? '#007bff' 
      : props.personaColor || '#f1f3f5'
  };
  color: ${props => props.isUser ? '#ffffff' : '#333'};
  font-size: 15px;
  line-height: 1.4;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  white-space: pre-wrap;
  word-wrap: break-word;
  
  ${props => !props.isUser && `
    border: 1px solid #e1e5e9;
  `}
`;

export const MessageInfo = styled.div`
  font-size: 12px;
  color: #666;
  margin-top: 4px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

export const InputContainer = styled.div`
  padding: 20px;
  border-top: 1px solid #e1e5e9;
  background-color: #f8f9fa;
`;

export const InputWrapper = styled.div`
  display: flex;
  gap: 12px;
  align-items: flex-end;
  max-width: 100%;
`;

export const MessageInput = styled.textarea`
  flex: 1;
  min-height: 44px;
  max-height: 120px;
  padding: 12px 16px;
  border: 1px solid #e1e5e9;
  border-radius: 22px;
  font-size: 15px;
  font-family: inherit;
  resize: none;
  outline: none;
  background-color: #ffffff;
  
  &:focus {
    border-color: #007bff;
    box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.1);
  }
  
  &::placeholder {
    color: #999;
  }
`;

export const SendButton = styled.button<{ disabled?: boolean }>`
  padding: 12px 24px;
  background-color: ${props => props.disabled ? '#ccc' : '#007bff'};
  color: white;
  border: none;
  border-radius: 22px;
  font-size: 15px;
  font-weight: 500;
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  transition: all 0.2s ease;
  
  &:hover {
    background-color: ${props => props.disabled ? '#ccc' : '#0056b3'};
  }
  
  &:active {
    transform: ${props => props.disabled ? 'none' : 'translateY(1px)'};
  }
`;

