  padding: 16px;
  margin-bottom: 8px;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  border: 2px solid ${props => props.isSelected ? (props.personaColor || '#007bff') : 'transparent'};
  background-color: ${props => props.isSelected ? '#f8f9ff' : '#f8f9fa'};
  
