  cursor: pointer;
  transition: all 0.2s ease;
  background-color: ${props => props.isSelected ? '#e3f2fd' : 'transparent'};
  border-left: 3px solid ${props => props.isSelected ? '#007bff' : 'transparent'};
  
  &:hover {
    background-color: ${props => props.isSelected ? '#e3f2fd' : '#f1f3f5'};
  }
