import React, { useState } from 'react';
import styled from 'styled-components';
import Message from './Message';
import MessageInput from './MessageInput';
import LoadingMessage from './LoadingMessage';
import { Message as MessageType } from '../types';

const InterpretationContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const InterpretationHeaderContainer = styled.div`
  padding: 20px 20px 10px 20px;
  border-bottom: 1px solid #e5e5e5;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const InterpretationHeader = styled.h2`
  margin: 0;
  color: #333;
  font-size: 24px;
  font-weight: 600;
`;

const NewInterpretationButton = styled.button`
  background: #007bff;
  color: white;
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 18px;
  transition: all 0.2s ease;

  &:hover {
    background: #0056b3;
    transform: scale(1.05);
  }
`;

const InterpretationContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const MessagesArea = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 0 20px;
  background: #fff;
`;

const ActionButtonsArea = styled.div`
  position: fixed;
  bottom: 90px;
  right: 30px;
  z-index: 1000;
  display: flex;
  gap: 10px;
`;

const ActionButton = styled.button`
  background: #6c757d;
  color: white;
  border: none;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 16px;
  transition: all 0.2s ease;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);

  &:hover {
    background: #5a6268;
    transform: scale(1.05);
  }
`;

interface InterpretationViewProps {
  interpretationData: {
    personaId: string;
    personaName: string;
    content: string;
    messages: MessageType[];
  };
  onSendMessage: (message: string) => Promise<boolean>;
  isLoading: boolean;
  onMenuAction: (action: string) => void;
  onCreateNewInterpretation?: () => void;
}

const InterpretationView: React.FC<InterpretationViewProps> = ({
  interpretationData,
  onSendMessage,
  isLoading,
  onMenuAction,
  onCreateNewInterpretation
}) => {
  const handleCopy = () => {
    navigator.clipboard.writeText(interpretationData.content);
    alert('Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
  };

