    <InterpretationContainer>
      <InterpretationHeaderContainer>
        <InterpretationHeader>
          {interpretationData.personaName} Ìï¥ÏÑù
        </InterpretationHeader>
        <NewInterpretationButton 
          onClick={onCreateNewInterpretation}
          title="ÏÉàÎ°úÏö¥ Ìï¥ÏÑù ÎßåÎì§Í∏∞"
        >
          ‚ú®
        </NewInterpretationButton>
      </InterpretationHeaderContainer>

      <InterpretationContent>
        <MessagesArea>
          {interpretationData.messages.map((msg, index) => (
            <Message
              key={index}
              message={msg}
              personas={{}}
            />
          ))}
          {isLoading && <LoadingMessage />}
        </MessagesArea>

        <MessageInput
          onSendMessage={onSendMessage}
          disabled={isLoading}
          placeholder={`${interpretationData.personaName}ÏóêÍ≤å Î©îÏãúÏßÄÎ•º Î≥¥ÎÇ¥ÏÑ∏Ïöî...`}
        />
      </InterpretationContent>

      <ActionButtonsArea>
        <ActionButton onClick={handleCopy} title="Î≥µÏÇ¨">
          üìã
        </ActionButton>
        <ActionButton onClick={() => onMenuAction('generateInterpretation')} title="Ìï¥ÏÑù">
          üîç
        </ActionButton>
        <ActionButton onClick={() => onMenuAction('generateProceed')} title="ÎÇòÏïÑÍ∞ÄÍ∏∞">
          üéØ
        </ActionButton>
        <ActionButton onClick={() => onMenuAction('generateSentence')} title="Î¨∏Ïû•">
          üìù
        </ActionButton>
      </ActionButtonsArea>
    </InterpretationContainer>
  );
};

export default InterpretationView;
