import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { qaApi, Question, QAAnswer } from '../api/qaApi';
import { FiMic as FiMicRaw } from 'react-icons/fi';
import { useRecorder } from '../hooks/useRecorder';

// react-icons 타입 문제 임시 대응
const FiMic = FiMicRaw as unknown as React.FC<React.SVGProps<SVGSVGElement>>;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px 20px;
`;

const GradientBox = styled.div<{ $height?: string }>`
  width: 100%;
  max-width: 600px;
  height: ${({ $height }) => $height || '150px'};
  background: linear-gradient(to bottom, #ffffff 0%, #e9ecef 100%);
  border-radius: 16px;
  padding: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  font-size: 18px;
  font-weight: 500;
  color: #495057;
`;

const AnswerBox = styled.textarea`
  width: 100%;
  height: 100%;
  border: none;
  resize: none;
  background: transparent;
  font-size: 16px;
  color: #212529;
  outline: none;
`;

const BottomBoxWrapper = styled.div`
  position: relative;
  width: 100%;
  max-width: 600px;
`;

const MicButton = styled.button`
  position: absolute;
  top: 12px;
  right: 12px;
  background: #fff;
  border: 1px solid #ced4da;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
`;

const FooterArea = styled.div`
  margin-top: 12px;
  width: 100%;
  max-width: 600px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CharCount = styled.span`
  font-size: 14px;
  color: #868e96;
`;

const SubmitButton = styled.button<{ $enabled: boolean }>`
  padding: 10px 20px;
  border-radius: 24px;
  border: 2px solid #000;
  background: #fff;
  font-size: 14px;
  font-weight: 600;
  cursor: ${({ $enabled }) => ($enabled ? 'pointer' : 'not-allowed')};
  opacity: ${({ $enabled }) => ($enabled ? 1 : 0.5)};
`;

const MIN_LEN = 10; // 최소 글자수 (요구: 0/100 표시, 예시로 10자)
const MAX_LEN = 100;

const QAView: React.FC<{ onComplete?: () => void }> = ({ onComplete }) => {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answer, setAnswer] = useState('');
  const [answers, setAnswers] = useState<QAAnswer[]>([]);
  const [loading, setLoading] = useState(true);
  const { isRecording, record, stopRecording } = useRecorder();

  useEffect(() => {
    const load = async () => {
      const res = await qaApi.getQuestions();
      if (res.data) {
        setQuestions(res.data);
      }
      setLoading(false);
    };
    load();
  }, []);

  const handleNext = async () => {
    const q = questions[currentIndex];
    const newAnswers = [...answers, { question_id: q.id, answer }];
    setAnswers(newAnswers);
    setAnswer('');
    if (currentIndex + 1 < questions.length) {
      setCurrentIndex(currentIndex + 1);
    } else {
      // 완료 → 서버 저장
      await qaApi.saveAnswers(newAnswers);
      if (onComplete) onComplete();
    }
  };

  const handleMicClick = async () => {
    if (isRecording) {
      stopRecording();
      return;
    }
    await record(async (blob) => {
      // send to backend
      const formData = new FormData();
      formData.append('file', blob, 'audio.webm');
      const resp = await fetch('/speech/transcribe', {
        method: 'POST',
        body: formData,
      });
      if (resp.ok) {
        const data = await resp.json();
        if (data.text) {
          setAnswer(prev => (prev ? prev + ' ' : '') + data.text);
        }
      }
    });
  };

  if (loading) return <Container>로딩 중...</Container>;
  if (questions.length === 0) return <Container>질문이 없습니다.</Container>;

  const currentQuestion = questions[currentIndex];
  const len = answer.length;
  const enabled = len >= MIN_LEN;

  return (
    <Container>
      <GradientBox>{currentQuestion.question}</GradientBox>

      <BottomBoxWrapper style={{ height: '150px', marginTop: '24px' }}>
        <GradientBox $height="150px">
          <AnswerBox
            value={answer}
            maxLength={MAX_LEN}
            onChange={(e) => setAnswer(e.target.value)}
            placeholder="답변을 입력하세요…"
          />
        </GradientBox>
        <MicButton onClick={handleMicClick} title={isRecording ? '녹음 중지' : '녹음 시작'}>
          {isRecording ? '■' : <FiMic width={18} height={18} />}
        </MicButton>
      </BottomBoxWrapper>

      <FooterArea>
        <CharCount>{len}/{MAX_LEN}</CharCount>
        <SubmitButton $enabled={enabled} disabled={!enabled} onClick={handleNext}>
          답변완료
        </SubmitButton>
      </FooterArea>
    </Container>
  );
};

export default QAView;
